mapper.insert:
insert
  into review
  	   (
  	   R_NO
  	 , MEM_NO
  	 , R_CONTENT
  	 , RATE
  	 , RV_STATUS
  	 , H_CODE
  	   )
values 
	   (
	   SEQ_RNO.nextval
	 ,  #{ memNo }
	 ,  #{ revContent }
	 , #{ rate }
	 , 'Y'
	 , #{ hosCode }
	   )

mapper.select:
select r_no , mem_no , nickname , r_content , r_date , rate , h_code , h_name , count(*) over() as review_count from review join member using (mem_no) join hospital using (h_code) where rv_status = 'Y'
and mem_no = #{memNo}
order by r_date desc

mapper.insert:
insert
  into h_order
values (
       SEQ_ODRNO.nextval
     , #{hosCode}
     , #{memNo}
     , #{diagnosisType}
     , #{addSymtom}
     , #{time}
     , #{orderDate}
     , DEFAULT
     , DEFAULT
	   )

mapper.update:
update h_order
   set order_status = 'N'
 where mem_no = #{memNo}
