  , mem_pwd	  
  , mem_name
  , nickname
  , email
  , address
  , phone	
  , buy_date
  )	
  values
  (
    seq_mno.nextval
  , 'M'
  , #{ memId }
  , #{ memPwd }
  , #{ memName }
  , #{ nickName }
  , #{ email }
  , #{ address }
  , #{ phone }
  , default
  )

mapper.update:
		update member
   		   set 
